(76, 1, '2018-11-05 13:37:41', '2018-11-05 06:37:41', '<table class="table table-bordered">\r\n<tbody>\r\n<tr>\r\n<td class="text-center text-bold" colspan="2">MÔ TẢ SẢN PHẨM</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Model</td>\r\n<td>SC100</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Áp dụng</td>\r\n<td>Dùng để vận chuyển người và vật tư tại các công trình xây dựng</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Chiều cao lắp dựng</td>\r\n<td>30m(20 đốt khung thân)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Lồng bao che</td>\r\n<td>Dạng thép lưới dập giãn caro sơn màu đỏ</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Công suất động cơ</td>\r\n<td>2x11Kw (Nhãn hiệu Motor: JINNUO MOTOR, Hộp số XINYA; Xuất xứ: Trung quốc).</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Chống rơi</td>\r\n<td>SAJ30-1.2 (Nhãn hiệu: SRIBS, Xuất xứ: Trung quốc)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Nguồn điện</td>\r\n<td>3Phase/380V/50Hz</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Hệ thống điện chính</td>\r\n<td>LS-VINA</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Tốc độ nâng</td>\r\n<td>36 m/phút</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Tải trọng nâng</td>\r\n<td>1000 kg(01 lồng)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Kích cỡ lồng</td>\r\n<td>3 x 1.3 x 2.5m (L x W x H)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Kích cỡ khung thân</td>\r\n<td>650 x 650 x 1508mm Sơn màu(Ống thép Ø76x4.5t)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Chiều dài giằng tường và số lượng</td>\r\n<td>2.2m-2.8m (5 Bộ)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Trọng lượng đốt khung thân</td>\r\n<td>150 kg</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">Tổng trọng lượng</td>\r\n<td>5200 kg/bộ</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<div>1. Lồng mặt đất</div>\r\n<div>2. Lồng nâng</div>\r\n<div>3. Cẩu tự lắp</div>\r\n<div>4. Bộ truyền động</div>\r\n<div>5. Đốt tiêu chuẩn</div>\r\n<div>6. Giằng</div>\r\n<div>7. Hộp điện nguồn</div></td>\r\n<td><img class="img-responsive" src="http://www.cokhigiahoang.com/Upload/editor/product/sc100.jpg" alt="sc100" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n&nbsp;\r\n<table class="table table-bordered">\r\n<tbody>\r\n<tr>\r\n<td class="text-center text-bold" colspan="5">THÔNG TIN KỸ THUẬT CỦA THIẾT BỊ</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="7">1</td>\r\n<td class="text-bold" rowspan="7">Lồng vận thăng</td>\r\n<td>Kích thước lồng (DxRxC)</td>\r\n<td colspan="2">3m x 1.3m x 2.5m</td>\r\n</tr>\r\n<tr>\r\n<td>Đáy và mái lồng</td>\r\n<td colspan="2">Thép tấm gân chống trượt dầy 4mm</td>\r\n</tr>\r\n<tr>\r\n<td>Vách lồng</td>\r\n<td colspan="2">Thép lưới dập giãn caro sơn màu (Đỏ hoặc vàng)</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="2">Cửa lồng</td>\r\n<td colspan="2">Thép lưới dập giãn Caro sơn màu (Đỏ hoặc vàng)</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2">Cửa trượt với khóa liên động cơ khí và điện từ.</td>\r\n</tr>\r\n<tr>\r\n<td>Khung dầm chính</td>\r\n<td colspan="2">Thép tấm uốn và định hình (Q235)</td>\r\n</tr>\r\n<tr>\r\n<td>Phủ bề mặt</td>\r\n<td colspan="2">Phun sơn (đỏ vàng hoặc xanh)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="3">2</td>\r\n<td class="text-bold" rowspan="3">Lồng bao che mặt đất</td>\r\n<td>Vật liệu</td>\r\n<td colspan="2">Thép lưới dập giãn Caro 30x50, Phun sơn (Đỏ hoặc vàng)</td>\r\n</tr>\r\n<tr>\r\n<td>Cửa</td>\r\n<td colspan="2">Đóng mở với khóa liên động cơ khí và điện tử</td>\r\n</tr>\r\n<tr>\r\n<td>Chiều cao</td>\r\n<td colspan="2">1.8 – 2.0m</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="4">3</td>\r\n<td class="text-bold" rowspan="4">Khung thân vận thăng</td>\r\n<td>Kích thước</td>\r\n<td colspan="2">650 x 650 x 1508mm</td>\r\n</tr>\r\n<tr>\r\n<td>Vật liệu</td>\r\n<td colspan="2">Ống thép Ø76x4.5t, V65x6t, Ø27x2.5t.</td>\r\n</tr>\r\n<tr>\r\n<td>Định vị, liên kết</td>\r\n<td colspan="2">Khớp côn H=15mm, Bulon M24x220</td>\r\n</tr>\r\n<tr>\r\n<td>Phủ bề mặt</td>\r\n<td colspan="2">Phun sơn (đỏ vàng hoặc xanh)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="2">4</td>\r\n<td class="text-bold" rowspan="2">Khung neo giằng</td>\r\n<td>Khoảng cách điều chỉnh</td>\r\n<td colspan="2">Trong phạm vi từ 2.2 - 2.8m khoảng cách từ thân đến tường, khoảng cách giữa 2 giằng từ 6-9m.</td>\r\n</tr>\r\n<tr>\r\n<td>Góc chiều ngang</td>\r\n<td colspan="2">± 8 độ</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">5</td>\r\n<td class="text-bold" colspan="2">Cùm đế</td>\r\n<td colspan="2">6-8 Cùm đế bằng kim loại phân bổ đều, sử dụng tắc-kê nở M16x200 mm để neo giữ đế thân vận thăng</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="6">6</td>\r\n<td class="text-bold" rowspan="6">Cơ cấu an toàn</td>\r\n<td>Chống rơi</td>\r\n<td colspan="2">SAJ30-1.2A (Nhãn hiệu SRIBS, Xuất xứ: Trung quốc)</td>\r\n</tr>\r\n<tr>\r\n<td colspan="3">Giới hạn trên, giới hạn dưới, chống rơi (bộ hạn chế tốc độ), ngắt nguồn 3 pha.</td>\r\n</tr>\r\n<tr>\r\n<td colspan="3">Nút nhấn dừng khẩn cấp.</td>\r\n</tr>\r\n<tr>\r\n<td colspan="3">Cơ cấu khóa liên động cơ khí và điện từ tại các cửa lồng.</td>\r\n</tr>\r\n<tr>\r\n<td colspan="3">Hệ thống bảo vệ thứ tự pha điện áp.</td>\r\n</tr>\r\n<tr>\r\n<td colspan="3">Chuông báo, lò xo giảm chấn.</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="4">7</td>\r\n<td class="text-bold" rowspan="4">Cơ chế vận hành</td>\r\n<td rowspan="2">Motor</td>\r\n<td>Đặc điểm kỹ thuật</td>\r\n<td>11 kw, nhãn hiệu: JINNUO MOTOR, xuất xứ: Trung quốc</td>\r\n</tr>\r\n<tr>\r\n<td>Cấp độ bảo vệ</td>\r\n<td>IP55</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="2">Hộp giảm tốc</td>\r\n<td rowspan="2">Đặc điểm kỹ thuật</td>\r\n<td>Trục vít, bánh vít. Tỷ số truyền 1:16</td>\r\n</tr>\r\n<tr>\r\n<td>Nhãn hiệu: XINYA, xuất xứ: Trung Quốc</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold" rowspan="3">8</td>\r\n<td class="text-bold" rowspan="3">Hệ thống điện</td>\r\n<td>Tủ điện chính</td>\r\n<td colspan="2">Nhãn hiệu: LS-VINA</td>\r\n</tr>\r\n<tr>\r\n<td>Loại Biến tần, Màn hình PLC</td>\r\n<td colspan="2">INVT- Trung Quốc</td>\r\n</tr>\r\n<tr>\r\n<td>Điện nguồn sử dụng</td>\r\n<td colspan="2">3Phase/380V/50Hz (có thể được thay đổi theo yêu cầu)</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">9</td>\r\n<td class="text-bold" colspan="2">Tốc độ nâng</td>\r\n<td colspan="2">36 m/ph</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">10</td>\r\n<td class="text-bold" colspan="2">Chiều cao lắp dựng tối đa</td>\r\n<td colspan="2">150 m</td>\r\n</tr>\r\n<tr>\r\n<td class="text-bold">11</td>\r\n<td class="text-bold" colspan="2">Cable điện động lực</td>\r\n<td colspan="2">3Cx16mm<sup>2</sup> + 1Cx10mm<sup>2</sup></td>\r\n</tr>\r\n</tbody>\r\n</table>', 'Vận thăng 01 lồng 1 tấn (SC 100 hoặc SCD 100)', '', 'publish', 'closed', 'closed', '', 'van-thang-01-long-1-tan-sc-100-hoac-scd-100', '', '', '2018-11-05 13:37:41', '2018-11-05 06:37:41', '', 0, 'http://localhost:8000/cokhigiahoang/git/?post_type=van-thang&#038;p=76', 0, 'van-thang', '', 0),
